import { RelayerActionV1 } from '../../../types/relayer-action.types';

/**
 * Valid Rhinestone action from real sample
 */
export const VALID_ACTION: RelayerActionV1 = {
  type: 'RelayerActionV1',
  id: '4878803956123138176169104804434956581267667078599884586455751597636993417216',
  timestamp: 1761691048,
  fill: {
    call: {
      chainId: 10,
      data: '',
      to: '0x000000000004598d17aad017bf0734a364c5588b',
      value: '0',
    },
    tokens: [
      {
        tokenAddress: '0x0b2c639c533813f4aa9d7837caf62653d097ff85',
        amount: '50000',
      },
    ],
    metadata: {
      settlementLayers: ['ECO'],
      tokensOut: [
        {
          amount: '50000',
          tokenAddress: '0x0b2c639c533813f4aa9d7837caf62653d097ff85',
        },
      ],
    },
  },
  claims: [
    {
      call: {
        chainId: 8453,
        data: '',
        to: '0x000000000004598d17aad017bf0734a364c5588b',
        value: '0',
      },
      tokens: [
        {
          tokenAddress: '0x833589fcd6edb6e08f4c7c32d4f71b54bda02913',
          amount: '51278',
        },
      ],
      beforeFill: true,
      metadata: {
        tokensIn: [
          {
            amount: '51278',
            tokenAddress: '0x833589fcd6edb6e08f4c7c32d4f71b54bda02913',
          },
        ],
        settlementLayer: 'ECO',
      },
    },
  ],
  metadata: {
    dryRun: true,
    userAddress: '0x5eeb0df30c63390735645a0788a373550ebd5707',
  },
};

/**
 * Create action with invalid router address in claim
 */
export function createActionWithInvalidClaimRouter(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    claims: [
      {
        ...VALID_ACTION.claims[0],
        call: {
          ...VALID_ACTION.claims[0].call,
          to: '0x1111111111111111111111111111111111111111',
        },
      },
    ],
  };
}

/**
 * Create action with invalid router address in fill
 */
export function createActionWithInvalidFillRouter(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    fill: {
      ...VALID_ACTION.fill,
      call: {
        ...VALID_ACTION.fill.call,
        to: '0x2222222222222222222222222222222222222222',
      },
    },
  };
}

/**
 * Create action with non-zero value in claim
 */
export function createActionWithNonZeroClaimValue(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    claims: [
      {
        ...VALID_ACTION.claims[0],
        call: {
          ...VALID_ACTION.claims[0].call,
          value: '1000000000000000000', // 1 ETH
        },
      },
    ],
  };
}

/**
 * Create action with non-zero value in fill
 */
export function createActionWithNonZeroFillValue(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    fill: {
      ...VALID_ACTION.fill,
      call: {
        ...VALID_ACTION.fill.call,
        value: '1000000000000000000', // 1 ETH
      },
    },
  };
}

/**
 * Create action with same source and destination chains
 */
export function createActionWithSameChains(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    fill: {
      ...VALID_ACTION.fill,
      call: {
        ...VALID_ACTION.fill.call,
        chainId: 8453, // Same as claim's chainId
      },
    },
  };
}

/**
 * Create action with invalid settlement layer
 */
export function createActionWithInvalidSettlementLayer(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    claims: [
      {
        ...VALID_ACTION.claims[0],
        metadata: {
          ...VALID_ACTION.claims[0].metadata,
          settlementLayer: 'ACROSS',
        },
      },
    ],
  };
}

/**
 * Create action with missing settlement layer
 */
export function createActionWithMissingSettlementLayer(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    claims: [
      {
        ...VALID_ACTION.claims[0],
        metadata: {
          ...VALID_ACTION.claims[0].metadata,
          settlementLayer: undefined,
        },
      },
    ],
  };
}

/**
 * Create action with no beforeFill claim
 */
export function createActionWithNoBeforeFillClaim(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    claims: [
      {
        ...VALID_ACTION.claims[0],
        beforeFill: false,
      },
    ],
  };
}

/**
 * Create action with empty claims array
 */
export function createActionWithEmptyClaims(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    claims: [],
  };
}

/**
 * Create action with multiple beforeFill claims
 */
export function createActionWithMultipleBeforeFillClaims(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    claims: [
      {
        ...VALID_ACTION.claims[0],
        beforeFill: true,
      },
      {
        ...VALID_ACTION.claims[0],
        beforeFill: true,
      },
    ],
  };
}

/**
 * Create action with invalid calldata
 */
export function createActionWithInvalidCalldata(): RelayerActionV1 {
  return {
    ...VALID_ACTION,
    claims: [
      {
        ...VALID_ACTION.claims[0],
        call: {
          ...VALID_ACTION.claims[0].call,
          data: '0xinvalidcalldata',
        },
      },
    ],
  };
}
